import React, { Component } from 'react';
// import logo from './logo.svg';
// import './App.css';
import 'typeface-roboto';
import QuoteMachine from './components/QuoteMachine';
import { random } from 'lodash';
import { Grid ,withStyles} from '@material-ui/core'
import Box from '@material-ui/core/Box';

const style = {
  container: {
    type: 'dark',
    alignItems: 'center',
    display: 'flex',
    height: '100vh'
  },
  Grid: {
    height: '140'
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [
        {
          "quote": "get faild",
          "author": "吴书琪"
        }
      ],
      selectedIndex: null
    }
    this.assignIndex = this.assignIndex.bind(this)
    this.setSelectedIndex = this.setSelectedIndex.bind(this)
  }

  nextClickHandler() {
    console.log("success");
  }

  componentDidMount() {
    fetch("https://gist.githubusercontent.com/natebass/b0a548425a73bdf8ea5c618149fe1fce/raw/f4231cd5961f026264bb6bb3a6c41671b044f1f4/quotes.json")

      .then(d => d.json())
      .then(quotes => this.setState({ quotes }, this.assignIndex))
      .catch(function (error) {
        console.log('fetch失败');
      })
  }


  get selectedQuote() {
    if (!this.state.quotes.length && !Number.isInteger(this.state.selectedIndex)) {
      return;
    }
    return this.state.quotes[this.state.selectedIndex];
  }

  // Pick a index for get quotes
  setSelectedIndex() {
    if (!this.state.quotes.length) {
      return;
    }
    return random(0, this.state.quotes.length - 1)
  }

  assignIndex() {
    this.setState({ selectedIndex: this.setSelectedIndex() })
  }
  render() {
    return (
      <Grid className={this.props.classes.container} id="quote-box" justify="center" container>
        <Grid item xs={12} lg={6} className={this.props.classes.Grid}>
          <h1 color="primary">Quote Machine</h1>
          <QuoteMachine assignIndex={this.assignIndex} selectedQuote={this.selectedQuote} />
        </Grid>
      </Grid>
    )
  }

}

export default withStyles(style)(App);
