import React, {Component} from 'react';
// import logo from './logo.svg';
import './App.css';
import Button from './components/Button.js'
import { random } from 'lodash'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quotes: [],
      selectedIndex: null
    }
    this.setSelectedIndex = this.setSelectedIndex.bind(this)
  }

  nextClickHandler() {
    console.log("success");
  }

  componentDidMount() {
    fetch("https://gist.githubusercontent.com/natebass/b0a548425a73bdf8ea5c618149fe1fce/raw/f4231cd5961f026264bb6bb3a6c41671b044f1f4/quotes.json")

    .then(d => d.json())
    .then(quotes => this.setState({ quotes }, ()=> {
      this.setState({ selectedIndex: this.setSelectedIndex() })
     }))
     .catch(function(error) {
       console.log('fetch失败' + error);
     })
    {/* setState, with a callback */}
    // .then( () => { this.setState({ selectedIndex:  this.setSelectedIndex() })
  }

  // Pick a index for get quotes
  setSelectedIndex() {
      if ( !this.state.quotes.length ) {
        return;
      }
      return random(0, this.state.quotes.length - 1)
  }

  render() {
    console.log(this.state.quotes)
    console.log(this.state.selectedIndex)
    return (
      <div className="App" id="quote-box">
        <h1>Quote Machine</h1>
        <Button
          buttonDisplayName="Quote Get"
          onClick={
            this.nextClickHandler
          }
        />

      </div>
    )
  }

}

export default App;
