import React from 'react';
import Button from './Button'

const QuoteMachine = (props) => {
    <React.Fragment>
        <h2>
          { props.selectedQuote ? props.selectedQuote.quote : props.state.quotes.quote}
        </h2>
        {/* <h3>
        { this.selectedQuote ? this.selectedQuote.author : this.state.quotes.author}
        </h3> */}
        {/* <div> { this.state.quote ? `"${ this.state.quotes.quote }" - ${ this.state.quotes.author}` : ""} </div> */}
        <Button
          buttonDisplayName="Quote Get"
          clickHandler={
            props.assignIndex
          }
        />
    </React.Fragment>
}

export default QuoteMachine;
